import React, { useState, useEffect } from 'react';
import { Platform, StyleSheet, Text, View, FlatList, PermissionsAndroid } from 'react-native';
import { fetch } from 'react-native-fetch';

export default function Login() {

    const [data, setData] = useState([]);

    useEffect(() => {
        PermissionsAndroid.request(PermissionsAndroid.PERMISSIONS.ACCESS_NETWORK_STATE).then(granted => {
            if (granted) {
                fetch('http://localhost:1348/user')
                    .then(response => response.json())
                    .then(users => setData(users));
            } else {
                console.log('Permission denied');
            }
        });
    }, []);

    return (
        <View >
            <Text>Welcome</Text>

            <FlatList
                data={data}
                keyExtractor={(item, index) => index.toString()}
                renderItem={({ item }) => (
                    <View style={{ backgroundColor: '#abc123', padding: 10, margin: 10 }}>
                        <Text style={{ color: '#fff', fontWeight: 'bold' }}>{item.name}</Text>
                        <Text style={{ color: '#fff' }}>{item.email}</Text>
                        <Text>City: {item.address.city}</Text>
                    </View>
                )}
            />
        </View>
    );
}
