import React, { Component } from 'react';
import { View, Text, StyleSheet, ScrollView, FlatList, TouchableOpacity } from 'react-native';
import { Card } from 'react-native-elements';
import { PDFDocument, PDFText } from 'react-native-pdf-lib';
import * as FileSystem from 'expo-file-system';


export default class YoutubeReports extends Component {
    state = {
        reports: [],
    };

    componentDidMount() {
        // Fetch YouTube reports for the logged-in user
        this.fetchReports();
    }

    fetchReports = () => {
        const { username } = this.props;
        fetch(`http://localhost:1348/get-data?username=${username}`)
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    this.setState({ reports: data.data });
                } else {
                    alert('Unable to fetch reports');
                }
            })
            .catch(error => {
                console.error('Error:', error);
                alert('An error occurred while fetching reports');
            });
    };

    downloadAsPDF = async () => {
        const { reports } = this.state;

        try {
            // Create a new PDF document
            const pdfDoc = await PDFDocument.create();

            // Add content to the PDF document
            const page = pdfDoc.addPage();
            const { width, height } = page.getSize();
            let y = height - 50;

            reports.forEach((report, index) => {
                const text = new PDFText()
                    .setText(`PÃ«rshkrimi: ${report.emertimi}`)
                    .setFontSize(12)
                    .setX(50)
                    .setY(y - index * 50);

                page.drawText(text);
            });

            // Generate the PDF file
            const pdfBytes = await pdfDoc.save();

            // Define the file path for saving the PDF
            const filePath = `${FileSystem.cacheDirectory}report.pdf`;

            // Write the PDF file to the device's file system
            await FileSystem.writeAsStringAsync(filePath, pdfBytes, {
                encoding: FileSystem.EncodingType.Base64,
            });

            // Open the PDF file for viewing/downloading
            await FileSystem.openAsync(filePath);
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred while generating the PDF');
        }
    };

    renderItem = ({ item }) => (
        <Card key={item.id} containerStyle={styles.card}>
            <Text style={styles.cardText}>PÃ«rshkrimi: {item.emertimi}</Text>
            <Text style={styles.cardText}>{this.props.username}: {item.totali}</Text>
            <Text style={styles.cardText}>Mbetja: {item.mbetja}</Text>
            <Text style={styles.cardText}>Totali: {item.totali}</Text>
            <Text style={styles.cardText}>Data: {item.data}</Text>
            <TouchableOpacity style={styles.button} onPress={this.downloadAsPDF}>
                <Text style={styles.buttonText}>Download as PDF</Text>
            </TouchableOpacity>
        </Card>
    );


    render() {
        const { reports } = this.state;

        return (
            <View style={styles.container}>
                <Text style={styles.title}>YouTube Reports</Text>

                <FlatList
                    data={reports}
                    renderItem={this.renderItem}
                    keyExtractor={item => item.id.toString()}
                />
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: '#F5FCFF',
    },
    title: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 20,
    },
    card: {
        marginBottom: 10,
    },
    cardText: {
        fontSize: 14,
        marginTop: 5,
    },
    button: {
        backgroundColor: '#3498db',
        padding: 10,
        borderRadius: 5,
        marginBottom: 10,
    },
    buttonText: {
        color: 'white',
        fontWeight: 'bold',
        textAlign: 'center',
    },
});
