import React, { Component } from 'react';
import { View, Text, StyleSheet } from 'react-native';

export default class YoutubeReports extends Component {
    state = {
        reports: [],
    };

    componentDidMount() {
        // Fetch YouTube reports for the logged-in user
        this.fetchReports();
    }

    fetchReports = () => {
        const { username } = this.props;
        fetch(`http://localhost:1348/reports?username=${username}`)
            .then(response => response.json())
            .then(data => {
                this.setState({ reports: data });
            })
            .catch(error => {
                console.error('Error:', error);
                alert('An error occurred while fetching reports');
            });
    };

    render() {
        const { reports } = this.state;

        return (
            <View style={styles.container}>
                <Text style={styles.title}>YouTube Reports</Text>
                {reports.map(report => (
                    <View key={report.id} style={styles.reportItem}>
                        <Text style={styles.reportTitle}>{report.title}</Text>
                        <Text style={styles.reportDetails}>Views: {report.views}</Text>
                        <Text style={styles.reportDetails}>Likes: {report.likes}</Text>
                    </View>
                ))}
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
    },
    title: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 20,
    },
    reportItem: {
        marginBottom: 10,
    },
    reportTitle: {
        fontSize: 16,
        fontWeight: 'bold',
    },
    reportDetails: {
        fontSize: 14,
        marginTop: 5,
    },
});
