// App.js
import React, { Component } from 'react';
import { View, Text } from 'react-native';
import LoginPage from './screens/Login.js';
import Dashboard from './screens/Dashboard.js';

export default class App extends Component {
  state = {
    isLoggedIn: false,
    username: '',
  };

  handleLogin = (username, password) => {
    const hash = md5(password); // Assuming you have the md5 hashing function available

    // Perform login API request
    fetch('http://localhost:1348/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username,
        password: hash,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          this.setState({ isLoggedIn: true, username });
        } else {
          alert('Invalid username or password');
        }
      })
      .catch((error) => {
        console.error('Login API error:', error);
      });
  };

  handleLogout = () => {
    // Perform logout API request
    fetch('http://localhost:1348/logout')
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          this.setState({ isLoggedIn: false, username: '' });
        } else {
          alert('Logout failed');
        }
      })
      .catch((error) => {
        console.error('Logout API error:', error);
      });
  };

  render() {
    const { isLoggedIn, username } = this.state;
    return (
      <View>
        <Text>Welcome</Text>
        {!isLoggedIn ? (
          <LoginPage handleLogin={this.handleLogin} />
        ) : (
          <Dashboard username={username} handleLogout={this.handleLogout} />
        )}
      </View>
    );
  }
}
