import React, { Component } from 'react';
import { View, Text, StyleSheet } from 'react-native';

export default class YoutubeReports extends Component {
    state = {
        reports: [],
    };

    componentDidMount() {
        // Fetch YouTube reports for the logged-in user
        this.fetchReports();
    }

    fetchReports = () => {
        const { username } = this.props;
        fetch(`http://localhost:1348/get-data?username=${username}`)
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    this.setState({ reports: data.data });
                } else {
                    alert('Unable to fetch reports');
                }
            })
            .catch(error => {
                console.error('Error:', error);
                alert('An error occurred while fetching reports');
            });
    };

    render() {
        const { reports } = this.state;

        return (
            <View style={styles.container}>
                <Text style={styles.title}>YouTube Reports</Text>
                <View style={styles.table}>
                    <View style={styles.tableHeader}>
                        <Text style={styles.tableHeaderText}>Numri i faturÃ«s</Text>
                        <Text style={styles.tableHeaderText}>Klientit</Text>
                        <Text style={styles.tableHeaderText}>Mbetja</Text>
                        <Text style={styles.tableHeaderText}>Totali</Text>
                        <Text style={styles.tableHeaderText}>Data</Text>
                    </View>
                    {reports.map(report => (
                        <View key={report.id} style={styles.tableRow}>
                            <Text style={styles.tableRowText}>{report.fatura}</Text>
                            <Text style={styles.tableRowText}>{report.klientit}</Text>
                            <Text style={styles.tableRowText}>{report.mbetja}</Text>
                            <Text style={styles.tableRowText}>{report.totali}</Text>
                            <Text style={styles.tableRowText}>{report.data}</Text>
                        </View>
                    ))}
                </View>
            </View>
        );

    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
    },
    title: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 20,
    },
    reportItem: {
        marginBottom: 10,
    },
    reportTitle: {
        fontSize: 16,
        fontWeight: 'bold',
    },
    reportDetails: {
        fontSize: 14,
        marginTop: 5,
    },
});
